HTML语言: domxml.html
<!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"> 使用DOM解析XML &lt;input type=&ldquo;button&rdquo; value=&ldquo;解析XML&rdquo; color: rgb(170,85,0)&gt;&ldquo;sendRequest();&rdquo;/&gt;
JavaScript语言: domxml.js
var XMLHttpReq;
var url = &ldquo;studentdata.xml&rdquo;;
function createXMLHttpRequest() {
if (window.XMLHttpRequest)
XMLHttpReq = new XMLHttpRequest();
else if (window.ActiveXObject) {
try {
XMLHttpReq = new ActiveXObject(&ldquo;Msxml2.XMLHTTP&rdquo;);
}
catch (e) {
try {
XMLHttpReq = new ActiveXObject(&ldquo;Microsoft.XMLHTTP&rdquo;);
}
